}

#increment when
-|-

 |
 -
 |

count <- 0
for (i in 1:length(pathlst)){
  ind <- (which(pathlst[[i]]=="|"))
  if(length(ind)>0){
   for(j in 1:length(ind)){
     if(pathlst[[i]][ind[j]-1] == "-" && pathlst[[i]][ind[j]+1] == "-")
       count <- count + 1
   }
  }
}

for (i in 2:(length(pathlst)-1)){
  ind <- (which(pathlst[[i]]=="-"))
  if(length(ind)>0){
    for(j in 1:length(ind)){
      if(pathlst[[i-1]][ind[j]] == "|" && pathlst[[i+1]][ind[j]] == "|")
        count <- count + 1
    }
  }
}

